import React from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { Header, Nav } from "./styels";
import { useTranslation } from "react-i18next"; // react-i18next hook for translations
import { useDispatch, useSelector } from "react-redux";
import { menuStatus } from "../../../../../store/actions/common";
import { GetIcon, LogoutIcon } from "../../../../../icons";
import LanguageTooltip from "../../../../elements/tooltip";
import { Logout, User } from "../header/styels";
import { clearLogin } from "../../../../../store/actions/login";
const Menu = (props) => {
  const { t } = useTranslation();
  const location = useLocation();
  const themeColors = useSelector((state) => state.themeColors);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  return (
    <>
      <Header className="hd">
        <User>{props.user.user.email}</User>
        <Logout
          onClick={() => {
            dispatch(clearLogin());
            navigate("/");
          }}
        >
          <LogoutIcon />
        </Logout>
        <LanguageTooltip></LanguageTooltip>
      </Header>
      <Nav theme={themeColors}>
        {/* Link to the home page */}
        {props.user.menu?.map((menuItem) => (
          <Link
            key={menuItem.label}
            onClick={() => {
              dispatch(menuStatus(menuItem.label));
            }}
            className={location.pathname === `/${menuItem.path}` ? "active" : ""}
            to={`/${menuItem.path}`}
          >
            <GetIcon icon={`home`} /> {t(menuItem.label)}
          </Link>
        ))}

      </Nav>
    </>
  );
};

export default Menu;
