import React, { useState } from "react";
import { logo } from "../../../../../images";
import { useTranslation } from "react-i18next";
import { useDispatch, useSelector } from "react-redux";
import { useLocation, useNavigate } from "react-router-dom";
import { Container, Logo, LogoContaner, Logout, MNav, Status, Title, User } from "./styels";
import { menuStatus } from "../../../../../store/actions/common";
import { CloseIcon, LogoutIcon, MenuIcon } from "../../../../../icons";
import LanguageTooltip from "../../../../elements/tooltip";
import { clearLogin } from "../../../../../store/actions/login";
const Header = (props) => {
  const { t } = useTranslation();
  const dispatch = useDispatch();
  const location = useLocation();
  const [headerText, setHeaderText] = useState("Home");
  const menuCurrentStatus = useSelector((state) => state.menuStatus);
  const navigate = useNavigate();
  useState(() => {
    setHeaderText(location.pathname);
  }, [location.pathname]);
  return (
    <Container>
      <LogoContaner>
        <MNav
          onClick={() => {
            dispatch(menuStatus());
          }}
        >
          {menuCurrentStatus ? <CloseIcon /> : <MenuIcon />}
        </MNav>
        <Logo src={logo} alt="logo" />
      </LogoContaner>
      <Status className="hm">
        <Title>{t(headerText)}</Title>
        <User>{props.user.user.email}</User>
        <Logout
          onClick={() => {
            dispatch(clearLogin());
            navigate("/");
          }}
        >
          <LogoutIcon />
          {t("logout")}
        </Logout>
        <LanguageTooltip></LanguageTooltip>
      </Status>
    </Container>
  );
};

export default Header;
