import React, { useEffect, useState } from "react";
import { useTranslation } from "react-i18next";
import { Container } from "../../../common/layout/styels";
import ListTable from "../../../../elements/list/list";
const Today = (props) => {
  const { t } = useTranslation();
  const [attributes] = useState([
    {
      type: "text",
      placeholder: "fullName",
      name: "fullName",
      validation: "",
      default: "",
      label: "fullName",
      required: true,
      view: true,
      add: true,
      update: true,
    },
    {
        type: "text",
        placeholder: "fullName",
        name: "fullName",
        validation: "",
        default: "",
        label: "fullName",
        required: true,
        view: true,
        add: true,
        update: true,
      },
    {
      type: "select",
      placeholder: "place",
      name: "place",
      validation: "",
      default: "",
      label: "place",
      required: true,
      view: true,
      add: true,
      update: true,
      apiType: "API",
      selectApi: "place/select",
    },
    {
      type: "select",
      placeholder: "shift",
      name: "shift",
      validation: "",
      default: "",
      label: "shift",
      required: true,
      view: true,
      add: true,
      update: true,
      apiType: "API",
      selectApi: "shift/select",
    },
  ]);
  //to update the page title
  useEffect(() => {
    document.title = `${t("Team")} - VSH Security`;
  }, [t]);


  // Use the useTranslation hook from react-i18next to handle translations
  // const parkingDuration = totalDuration > 120 ? (days > 0 ? days + `d, ` : ``) + (hours > 0 ? hours + `h, ` : ``) + (minutes + t("m")) : totalDuration.toFixed(0) + ` ` + t("minutes");
  return (
    <Container className="noshadow">
      <ListTable {...props} api={`attendace/today`} itemTitle={`title`} shortName={`today`} addPrivilege={false} delPrivilege={false} updatePrivilege={false} attributes={attributes}></ListTable>
     </Container>
  );
};
// exporting the page with parent container layout..
export default Today;
