import React from "react";
import { logo } from "../../../../../images";
import { useTranslation } from "react-i18next";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { Container, Logo, LogoContaner, Logout, MNav, Status, Title, User } from "./styels";
import { menuStatus } from "../../../../../store/actions/common";
import { CloseIcon, GetIcon, LogoutIcon, MenuIcon } from "../../../../../icons";
import LanguageTooltip from "../../../../elements/tooltip";
import { clearLogin } from "../../../../../store/actions/login";
const Header = (props) => {
  const { t } = useTranslation();
  const dispatch = useDispatch();
  const menuCurrentStatus = useSelector((state) => state.menuStatus);
  const currentMenu = useSelector((state) => state.currentMenu);
  console.log("currentMenu",currentMenu)
  const navigate = useNavigate();
  return (
    <Container>
        <MNav
          onClick={() => {
            dispatch(menuStatus());
          }}
        >
          {menuCurrentStatus ? <GetIcon icon={'close'} /> : <MenuIcon />}
        </MNav>
      <Status>
        <Title>{currentMenu}</Title>
        <User>{props.user.user.email}</User>
        <Logout
          onClick={() => {
            dispatch(clearLogin());
            navigate("/");
          }}
        >
          <LogoutIcon />
          {t("logout")}
        </Logout>
        <LanguageTooltip></LanguageTooltip>
      </Status>
    </Container>
  );
};

export default Header;
