import React from "react";
import { BrowserRouter, Route, Routes } from "react-router-dom";

import Page404 from "../public/page404"; // 404 page component

import Switch from "./switch";
import { useSelector } from "react-redux";

const PageRouter = () => {
  const user = useSelector((state) => state.login);
  return (
    // BrowserRouter is used for client-side routing
    <BrowserRouter>
      {/* Routes component to define different routes */}
      <Routes>
        {/* Route for the root path, renders the Login component */}
        <Route path="/" element={<Switch page="login" />} />
        {user?.data?.menu?.map((menu) => {
          if (menu.submenus?.length > 0) {
            menu.submenus.map((submenu) => {
              return <Route key={`${submenu._id}`} path={`${submenu.path}`} element={<Switch page={submenu.element} />} />;
            });
          }
          return <Route key={`${menu._id} `} path={`${menu.path}`} element={<Switch page={menu.element} />} />;
        })}
        <Route path="*" element={<Page404 />} />
      </Routes>
    </BrowserRouter>
  );
};

// Export PageRouter component as the default export of the module
export default PageRouter;
